/** @type {FormatterObjects} */
export const defaultFormatters: FormatterObjects
export type PhrasingContent = import('mdast').PhrasingContent
export type Contributor =
  import('./get-contributors-from-package.js').Contributor
export type FormatterObject = {
  /**
   * Text in the header row that labels the column for this field.
   */
  label?: string | undefined
  /**
   * Whether to ignore these fields.
   */
  exclude?: boolean | undefined
  /**
   * How to format the cell.
   */
  format?: Format | undefined
}
export type Format = (
  value: unknown,
  key: string,
  contributor: Contributor
) => null | undefined | string | PhrasingContent | Array<PhrasingContent>
export type FormatterObjects = Record<string, FormatterObject>
